const smoothScroll=()=>{document.querySelectorAll(".-scroll").forEach(e=>{e.addEventListener("click",o=>{o.preventDefault();const n="button"===o.target.localName?e.getAttribute("href").split("#")[1]:o.target.href.split("#")[1],c=document.getElementById(n);requestAnimationFrame(e=>{const o=e||(new Date).getTime(),n=o,l=window.pageYOffset,r=c.getBoundingClientRect().top;t(n,o,1200,r,l)})})});const t=(e,o,n,c,l)=>{const r=o-e;let s=r/n;const a=(t=>t*t*t)(s=Math.min(s,1));window.scroll(0,l+c*a),r<n&&requestAnimationFrame(o=>{const r=o||(new Date).getTime();t(e,r,n,c,l)})}};window.onload=function(){smoothScroll();const t=document.querySelectorAll(".downarrow");for(x of t){let t="home"===x.parentElement.id?"portfolio":"portfolio2";x.firstElementChild.setAttribute("href",`#${t}`)}};const navLink=document.querySelectorAll(".nav__link"),projSect=document.querySelector(".project-section"),contSect=document.querySelector(".contact");function addClass(t,e){t.classList.add(e)}function removeClass(t,e){t.classList.remove(e)}document.addEventListener("scroll",function(){const t=projSect.offsetTop,e=contSect.offsetTop;window.scrollY>t&&window.scrollY<e?navLink.forEach(function(t){addClass(t,"rednav"),removeClass(t,"whitenav")}):navLink.forEach(function(t){addClass(t,"whitenav"),removeClass(t,"rednav")})});